var buttonImage = document.getElementById("buttonImage");
var elementImage = document.getElementById("elementImage");

buttonImage.addEventListener("click", function() {
if (elementImage.style.display === "none") {
    elementImage.style.display = "block";
} else {
    elementImage.style.display = "none";
}
});

